mapper.select:
select count(*)
from money
where house_id = #{houseId}

select id, house_id, partA_login_name, partB_login_name, pay_date, pay_money,
status, partA_confirm, partB_confirm, prompt,  partA_confirm_date,
partB_confirm_date
from money
where house_id = #{houseId}
limit #{page.start},#{page.limit}

select count(*)
from house_transfer_association as t1
left join house_info as t2 on t1.house_id = t2.house_id


    and t2.province_code = #{provinceCode}


    and t2.city_code = #{cityCode}


    and t2.district_code = #{districtCode}


    and t1.create_by = #{checkBy}


    and date_format(t1.create_time,'%Y-%m-%d') between date_format(#{startCreateDate},'%Y-%m-%d') and date_format(#{endCreateDate},'%Y-%m-%d')

  and ( t1.status = '正在出租' or t1.status = '已结束' )

select t1.transfer_id as id, t1.house_id as houseId, t1.partA_login_name as partALoginName, t1.partB_login_name as partBLoginName,t1.create_by as createBy,t1.create_time as createTime, t1.status as status, t2.province_code as provinceCode ,t4.province_name as provinceName, t2.city_code as cityCode ,t5.city_name as cityName,t2.district_code as districtCode, t6.district_name as districtName, t3.partA_intermediary_costs as partAIntermediaryCosts, t3.partB_intermediary_costs as partBIntermediaryCosts from house_transfer_association as t1 left join house_info as t2 on t1.house_id = t2.house_id left join contract as t3 on t1.house_id = t3.house_id left join provinces as t4 on t2.province_code = t4.province_code left join cities as t5 on t2.city_code = t5.city_code left join districts as t6 on t2.district_code = t6.district_code
and t2.province_code = #{queryMoneyDTO.provinceCode}
and t2.city_code = #{queryMoneyDTO.cityCode}
and t2.district_code = #{queryMoneyDTO.districtCode}
and t1.create_by = #{queryMoneyDTO.checkBy}
and date_format(t1.create_time,'%Y-%m-%d') between date_format(#{queryMoneyDTO.startCreateDate},'%Y-%m-%d') and date_format(#{queryMoneyDTO.endCreateDate},'%Y-%m-%d')
and ( t1.status = '正在出租' or t1.status = '已结束' )
limit #{page.start},#{page.limit}
